class ConfirmDialog{constructor(options){this.language={english:{message:"Confirm this action?",ok:"OK",cancel:"Cancel"},spanish:{message:"¿Confirmar esta acción?",ok:"Aceptar",cancel:"Cancelar"},german:{message:"Diese Aktion bestätigen?",ok:"OK",cancel:"Abbrechen"},french:{message:"Confirmer cette action ?",ok:"OK",cancel:"Annuler"},portuguese:{message:"Confirmar esta ação?",ok:"OK",cancel:"Cancelar"}};this.background=document.createElement("div");this.background.classList.add("background");this.message=document.createElement("div");this.message.classList.add("message");this.message.textContent=this.language[options.lang].message;this.subtitle=document.createElement("div");this.subtitle.classList.add("subtitle");this.subtitle.textContent=options.subtitle??"";this.actions=document.createElement("div");this.actions.classList.add("actions");this.confirmButton=document.createElement("button");this.confirmButton.textContent=this.language[options.lang].ok;this.cancelButton=document.createElement("button");this.cancelButton.textContent=this.language[options.lang].cancel;this.background.style.position="fixed";this.background.style.display="flex";this.background.style.justifyContent="center";this.background.style.alignItems="center";this.background.style.top="0";this.background.style.left="0";this.background.style.width="100%";this.background.style.height="100%";this.background.style.zIndex="9999";this.background.style.backgroundColor="rgba(0, 0, 0, 0.4)";this.message.style.position="relative";this.message.style.display="flex";this.message.style.flexDirection="column";this.message.style.alignItems="center";this.message.style.justifyContent="center";this.message.style.gap="25px";this.message.style.backgroundColor="#f2f2f2";this.message.style.borderRadius="5px";this.message.style.fontSize="1.4rem";this.message.style.width="350px";this.message.style.height="150px";this.message.style.margin="auto";this.message.style.padding="30px";this.message.style.transition="transform 0.3s ease-in-out, opacity 0.3s ease-in-out";this.message.style.transform="translateY(-25px)";this.message.style.opacity="0.3";this.actions.style.display="flex";this.actions.style.justifyContent="end";this.actions.style.gap="15px";this.actions.style.marginTop="10px";this.subtitle.style.fontSize="1.1rem";this.confirmButton.style.padding="8px 16px";this.confirmButton.style.borderRadius="4px";this.confirmButton.style.backgroundColor="#007BFF";this.confirmButton.style.color="#fff";this.confirmButton.style.border="none";this.confirmButton.style.cursor="pointer";this.confirmButton.style.outline="none";this.confirmButton.style.fontSize="1.05rem";this.cancelButton.style.padding="8px 16px";this.cancelButton.style.borderRadius="4px";this.cancelButton.style.backgroundColor="#808080";this.cancelButton.style.color="#fff";this.cancelButton.style.border="none";this.cancelButton.style.cursor="pointer";this.cancelButton.style.outline="none";this.cancelButton.style.fontSize="1.05rem";this.confirmButton.addEventListener("mouseover",function(){this.style.backgroundColor="#0056b3"});this.confirmButton.addEventListener("mouseout",function(){this.style.backgroundColor="#007BFF"});this.cancelButton.addEventListener("mouseover",function(){this.style.backgroundColor="#5a5a5a"});this.cancelButton.addEventListener("mouseout",function(){this.style.backgroundColor="#808080"});this.background.appendChild(this.message);this.message.appendChild(this.actions);this.actions.appendChild(this.cancelButton);this.actions.appendChild(this.confirmButton);if(options.subtitle){this.message.insertBefore(this.subtitle,this.actions)}}addToDOM(){document.body.appendChild(this.background);setTimeout(()=>{this.message.style.transform="translateY(0px)";this.message.style.opacity="1"},10)}show(){this.addToDOM();return new Promise((resolve,_)=>{this.confirmButton.addEventListener("click",()=>{this.resolvePromise({ok:true})});this.cancelButton.addEventListener("click",()=>{this.resolvePromise({ok:false})})})}resolvePromise(status){this.message.style.transform="translateY(-25px)";this.message.style.opacity="0.3";setTimeout(()=>{document.body.removeChild(this.background);resolve(status)},300)}}